package curso_java;

public class Math_1702 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Absoluto: "+Math.abs(-10));
		System.out.println("Raiz: "+Math.sqrt(9));
		System.out.println("Cubo: "+Math.pow(2, 8));
		System.out.println("Arredondar: "+Math.ceil(6.1));
		System.out.println("Arredondamento para baixo: "+Math.floor(4.9));
		System.out.println("MAior valor: "+Math.max(6,9));
		System.out.println("Menor valor: "+Math.min(9,4));
		
		System.out.println("Coseno: "+Math.cos(1));
		System.out.println("Tangente: "+Math.tan(1));
		System.out.println("Seno:"+Math.sin(1));
		
		System.out.println("Randon: "+ (int) (Math.random()* 51));
	}

}
